import { Component } from "@angular/core";

@Component({
  selector: "app-harness",
  templateUrl: "./harness.component.html",
  styleUrls: ["./harness.component.css"],
})
export class HarnessComponent {
  custfieldLabelA = "Category";
  custfieldLabelB = "ID number";
  custplaceTextA = "enter category";
  custplaceTextB = "enter id number";

  monitor = "watch here";

  constructor() {}

  ngOnInit() {}

  handleCustomEvent(e: any) {
    this.monitor = e.data;
  }
}
<section>
  <h3>Default Data Component Instance</h3>
  <app-data (myEvent)="handleCustomEvent($event)"></app-data>

  <h3>Data Component Instance with config options set</h3>
  <app-data
    fieldLabelA="custfieldLabelA"
    fieldLabelB="custfieldLabelB"
    placeTextA="Enter Category ID"
    placeTextB="custplaceTextB"
    (myEvent)="handleCustomEvent($event)"
  ></app-data>
</section>
<hr />
<aside>{{ monitor | json }}</aside>
